User.destroy_all
Category.destroy_all
Business.destroy_all
BusinessesCategory.destroy_all
Review.destroy_all

all_first_names.length.times do |i|
  User.create({
    f_name: all_first_names[i],
    l_name: all_last_names[i],
    username: all_last_names[i],
    email: "#{all_first_names[i].downcase}_#{all_last_names[i].downcase}@email.com",
    password: "#{all_last_names[i].downcase}_password"
  })
end

User.create({
  f_name: "Guest",
  l_name: "User",
  username: "User",
  email: "guest_user@email.com",
  password: "user_password"
})

all_categories.each do | category_alias, category_title|
  Category.create!({ alias: category_alias, title: category_title})
end

all_businesses_by_category.each do |category_alias, biz_arr|
  biz_arr.each do |biz|
    Business.create({
      title: biz["name"],
      alias: biz["name"].downcase.gsub(/[^0-9a-z]/, ''),
      user_id: User.find_by_l_name(all_last_names.sample).id,
      address1: biz["location"]["display_address"].first,
      address2: biz["location"]["display_address"].last,
      rating: biz["rating"],
      review_count: biz["review_count"],
      yelp_url: biz["url"],
      phone: biz["display_phone"],
      lat: biz["coordinates"]["latitude"],
      lng: biz["coordinates"]["longitude"],
      price: biz["price"],
      image_url: biz["image_url"]
    })

    current_biz = Business.find_by(title: biz["name"])

    if current_biz
      BusinessesCategory.create({
        business_id: current_biz.id,
        category_id: Category.find_by(alias: category_alias).id
      })

      unless Review.find_by(business_id: current_biz.id)
        all_reviews.sample(3).each do |review|
          Review.create(
          business_id: current_biz.id,
          user_id: User.find_by_l_name(all_last_names.sample).id,
          content: review,
          rating: (1..5).to_a.sample
          )
        end
      end

    end


  end
end
